<p>C++<br></p>

<p></p><pre><code class="lang-cpp">#include &lt;iostream&gt;
using namespace std;


int bcnn( int a, int b, int c);

int main() {
    int a, b, c;
    cout &lt;&lt; "Nhap 3 so a, b, c: ";
    cin &gt;&gt; a &gt;&gt; b &gt;&gt; c;

    cout &lt;&lt; "BCNN(a, b, c) = : " &lt;&lt; bcnn(a, b,c);

    return 0;
}

// Tìm BCNN của 3 số
int bcnn( int a, int b, int c) {
     // tìm số lớn nhất trong 3 số
     int greater, bcnn;
     greater = (a &gt; b &amp;&amp; a &gt; c ) ? a : ((b &gt; c) ? b : c);

     // chạy và tìm BCNN
 while (true) {
    if (greater%a == 0 &amp;&amp; greater%b == 0 &amp;&amp; greater%c == 0){
        bcnn = greater;
        break;
    }
    ++greater;
 }
}</code></pre>

