<p></p><pre><code class="lang-auto">#include&lt;math.h&gt;
int uCLN(int a, int b)
{
	a = abs(a); // trị tuyệt đối cho số âm
	b = abs(b);  // trị tuyệt đối cho số âm
	if (a == 0 &amp;&amp; b != 0)
	{
		return b;
	}
	else if (a != 0 &amp;&amp; b == 0)
	{
		return a;
	}
	while (a != b)
	{
		if (a &gt; b)
		{
			a -= b;
		}
		else
		{
			b -= a;
		}
	}
	return a;

}</code></pre>
