<p><em>Các hàm trong chương trinh</em><br><strong>UCLN2</strong><br></p>

<p></p><pre><code class="lang-auto">template&lt; class T&gt; UCLN2(T a, T b);</code></pre>

<p><em>Hàm này sẽ tính Ước chung lớn nhất cuả 2 số a,b</em></p>

<hr>

<p><em>đây là toàn bộ code</em><br></p>

<p></p><pre><code class="lang-auto">#include &lt;cstdio&gt;
#include &lt;cstdlib&gt;
#include &lt;cstdarg&gt;
#include &lt;iostream&gt;
using namespace std;

#define CONCAT(name,type) name##_##type
#define CREATE(name,type) \
type CONCAT(name,type)(){\
    type tmp;\
    cin&gt;&gt;tmp;\
    return tmp;\
}
CREATE(get,int)
CREATE(get,long)
CREATE(get,char)
CREATE(get,double)
CREATE(get,float)

template &lt;class T&gt;
T UCLN2(T a,T b){
    if(a==0) return b;
    return UCLN2(b%a,a);
}
template &lt;class T&gt;
T BCNN2(T a,T b){
    return a*b/UCLN2(a,b);
}
template &lt;class T&gt;
T UCLN(int n,...){
    T a,b;
    va_list vl;
    va_start(vl,n);
    a=va_arg(vl,T);
    for(int i=1;i&lt;n;++i){
        b=va_arg(vl,T);
        a=UCLN2&lt;T&gt;(a,b);
    }
    return a;
}
template &lt;class T&gt;
T BCNN(int n,...){
    T a,b;
    va_list vl;
    va_start(vl,n);
    a=va_arg(vl,T);
    for(int i=1;i&lt;n;++i){
        b=va_arg(vl,T);
        a=BCNN2&lt;T&gt;(a,b);
    }
    return a;
}

int main(){
    long a=BCNN&lt;long&gt;(3,get_long(),get_long(),get_long());
    printf("%ld",a);
    return 0;
}

</code></pre>

